import React, { Component } from 'react';
import './css/softwareng.css';
import './css/font-awesome.min.css';
import { withRouter , NavLink } from "react-router-dom";
import Routes from "./routes";


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      toggled: 1
    };

  }

  toggleMenu = toggle => {
    this.setState({
      toggled: !this.state.toggled
    });
  }

/*
    <div className={this.state.toggled ? "menu-toggle" : "menu-toggle on"} onClick={this.toggleMenu}>
      <div className="one"></div>
      <div className="two"></div>
      <div className="three"></div>
    </div>
    <nav>
      <ul role="navigation" className={this.state.toggled ? "hidden" : ""} id="menu-stuff">
        <li><NavLink to="/software">software engineering</NavLink></li>
        <li><NavLink to="/physics">physics</NavLink></li>
        <li><NavLink to="/cv">resume</NavLink></li>
        <li><NavLink to="/contact">contact</NavLink></li>
      </ul>
    </nav>
*/

  render() {
  return (
    <div className="noisy">
      <div class="frame">
        <div class="piece output">
          <div className="Menu">
            <p><NavLink to="/software">software engineering</NavLink><a href="/index.php">Homepage</a> <a href="/physics.php">Physics</a> <a href="/contact.php">Contact</a></p>
          </div>
          <Routes />
        </div>
        <div class="piece scanlines noclick"></div>
        <div class="piece glow noclick"></div>
      </div>
    </div>
  );
}
}
export default withRouter(App);
