import React, { Component } from 'react';
import './css/main2.css';
import './css/font-awesome.min.css';
import './css/menu.css';
import { withRouter , NavLink } from "react-router-dom";
import Routes from "./routes";


class App extends Component {

  constructor(props) {
    super(props);
    this.myPassions()
    this.state = {
      toggled: 1,
      passion: ""
    };

  }

  toggleMenu = toggle => {
    this.setState({
      toggled: !this.state.toggled
    });
  }

  //Found on https://stackoverflow.com/a/39914235
  sleep(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  //Found on https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Math/random
  getRandomInt(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }


  //Function used to iterate through the hobbies and things I am generally passionate about on the homescreen
  async myPassions() {
    var passions = ["Physics", "Programming", "Football","Quantum<br>Mechanics","Astrophysics","Boxing","Particle Physics","Linux","FOSS","Group<br>Theory","Hackathons","Cybersecurity","Bug<br>hunting","Exploits","Networks","Penetration<br>testing"];
    var i=1;
    while(i==1){
      var num=this.getRandomInt(0,passions.length -1);
      this.setState({
      passion: passions[num]
    }); 
    await this.sleep(1000);
    }
  }

  render() {
  return (
    <div>
      <div className="menu-section">
        <div className={this.state.toggled ? "menu-toggle" : "menu-toggle on"} onClick={this.toggleMenu}>
          <div className="one"></div>
          <div className="two"></div>
          <div className="three"></div>
        </div>
        <nav>
          <ul role="navigation" className={this.state.toggled ? "hidden" : ""} id="menu-stuff">
            <li><NavLink to="/software">software engineering</NavLink></li>
            <li><NavLink to="/physics">physics</NavLink></li>
            <li><NavLink to="/cv">resume</NavLink></li>
            <li><NavLink to="/contact">contact</NavLink></li>
          </ul>
        </nav>
      </div>

      <div id="signature">

      </div>

      <div className="middle-circle">
      <h1 className="giant">Hey!</h1>
      <p className="large">I'm <strong>Enrico.</strong></p>
      <div className="inline-div"><p className="large">I'm passionate about</p> <p className="large" id="passions">{this.state.passion}</p></div>
      <div id="social-div">
        <ul className="social">
          <li><a href="https://www.linkedin.com/in/enrico-zammit-lonardelli/" className="icon alt fa-linkedin"></a></li>
          <li><a href="https://github.com/enricozammitlon" className="icon alt fa-github"></a></li>
        </ul>
        </div>
      </div>
    </div>
  );
}
}
export default withRouter(App);
