import React, { Component } from "react";


export default class Section extends Component {
   constructor(props) {
        super(props);
        this.state = {
            active: this.props.active,
        };
    }
    toggleClass= toggle => {
    	this.props.toggle
    	this.setState({
    		active:1
    	})
    };
//        	<li class="active" onClick={this.getCurrentSection("aims")}>Aims</li>


    render() {
        return (
            <li 
                className={this.props.active ? 'active' : ""} 
                onClick={this.toggle} 
            >
            {this.props.text}
            </li>
        )
  }
}
