import React, { Component } from "react";
import '../css/terminal.css';

export default class Temrinal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      history: ["command1","command2","command3"],
      currentCommand: ""
    };
  }
/*
  componentDidUpdate(prevProps,prevState) {
    if(prevProps.currentCommand!=this.props.currentCommand){
      var history = this.state.history
      history.push(this.state.currentCommand)
      this.setState({
        history: history,
        currentCommand:this.props.currentCommand
      })
    }
  }
*/

  renderHistory(){
    var components=[]
    var history=this.state.history
    for (var command in history) {
      components.push(<li>{history[command]}</li>)
    }
    return components
  }

  handleKeyDown = e => {
    if (e.key === 'Enter') {
       var history = this.state.history
        history.push(this.state.currentCommand)
        this.setState({
          history: history,
          currentCommand:""
        })
    }
    else{
      this.setState({
        currentCommand:this.state.currentCommand+e.key
      })
    }
  }

  render() {
    return (
      <div>

        <div className="history">
          {this.renderHistory()}
        </div>

        <div className="currentCommand">
          <li><input onKeyDown={this.handleKeyDown} value={this.state.currentCommand}/></li>
        </div>

      </div>
    )
  }
}
