import React, { Component } from "react";
import '../css/terminal.css';

export default class Temrinal extends Component {
  constructor(props) {
    super(props);
    this.state = {
    	history: ["command1","command2","command3"],
        currentCommand: this.props.currentCommand
    };
  }

  componentDidUpdate(prevProps,prevState) {
    if(prevProps.currentCommand!=this.props.currentCommand){
      var history = this.state.history
      history.push(this.state.currentCommand)
      this.setState({
        history: history,
        currentCommand:this.props.currentCommand
      })
    }
  }

    toggleClass= toggle => {
    	this.props.toggle(toggle,this.props.text)
    	this.setState({
    		active:1
    	})
    };
//        	<li class="active" onClick={this.getCurrentSection("aims")}>Aims</li>

	renderHistory(){
		var components=[]
		var history=this.state.history
		for (var command in history) {
			components.push(<li>{history[command]}</li>)
		}
		return components
	}

  handleKeyDown = e => {
  if (e.key === 'Enter') {
    console.log('do validate');
  }
  else{
    this.setState({
      currentCommand:this.state.currentCommand+e.key
    })
  }
  }

  render() {
    return (
      <div>

        <div className="history">
        	{this.renderHistory()}
        </div>

        <div onKeyDown={this.handleKeyDown} className="currentCommand">
        	<li>{this.state.currentCommand}</li>
        </div>

      </div>
    )
  }
}
